from cs1robots import *

# Your code must work for all world files below.

# load_world('worlds/trash3.wld')
load_world('worlds/trash4.wld')

bot = Robot()
bot.set_trace('blue')
bot.set_pause(0.3)

def turn_right():
    for i in range(3):
        bot.turn_left()

def pick_all_beepers():
    while bot.on_beeper():
        bot.pick_beeper()

def drop_all_beepers():
    while bot.carries_beepers():
        bot.drop_beeper()


def move_and_pick_all():
    bot.move()
    pick_all_beepers()

def move_to_wall():
    while bot.front_is_clear():
        move_and_pick_all()

def round_trip():
    move_to_wall()
    turn_right()
    if bot.front_is_clear():
        move_and_pick_all()
        turn_right()
        move_to_wall()
        bot.turn_left()
        if bot.front_is_clear():
            move_and_pick_all()
            bot.turn_left()

def smart_zigzag():
    pick_all_beepers()
    if bot.left_is_clear():
        bot.turn_left()
        while bot.front_is_clear():
            round_trip()
    else:
        move_to_wall()

def face_north():
    while not bot.facing_north():
        bot.turn_left()

def move_to_corner():
    face_north()
    bot.turn_left()
    move_to_wall()
    bot.turn_left()
    move_to_wall()

smart_zigzag()
move_to_corner()
drop_all_beepers()

import cs1robots
print(cs1robots.helper.get_all_beepers_dict())
